tatus) {\r\n\r\n              if (status == google.maps.DirectionsStatus.OK) {\r\n\r\n                directionsDisplay.setDirections(response);\r\n\r\n              }\r\n\r\n            });\r\n\r\n          }\r\n\r\n      var map;\r\n\r\n      var image = "";\r\n\r\n      var infowindow = new google.maps.InfoWindow();\r\n\r\n      function initialize() {\r\n\r\n        map = new google.maps.Map(document.getElementById("directionsMap"), myOptions);\r\n\r\n            var LatLngList = Array(new google.maps.LatLng (33.465906,-111.902533));\r\n\r\n            var bounds = new google.maps.LatLngBounds();\r\n\r\n\r\n\r\n            // Extend the bounds for each marker\r\n\r\n            for (var i = 0, LtLgLen = LatLngList.length; i < LtLgLen; i++) {\r\n\r\n              bounds.extend(LatLngList[i]);\r\n\r\n            }\r\n\r\n\r\n\r\n            // Don''t zoom in too far on only one marker\r\n\r\n            if (bounds.getNorthEast().equals(bounds.getSouthWest())) {\r\n\r\n               var extendPoint1 = new google.maps.LatLng(bounds.getNorthEast().lat() + 0.01, bounds.getNorthEast().lng() + 0.01);\r\n\r\n               var extendPoint2 = new google.maps.LatLng(bounds.getNorthEast().lat() - 0.01, bounds.getNorthEast().lng() - 0.01);\r\n\r\n               bounds.extend(extendPoint1);\r\n\r\n               bounds.extend(extendPoint2);\r\n\r\n            }\r\n\r\n\r\n\r\n            map.fitBounds(bounds);\r\n\r\n            google.maps.event.addDomListener(window, ''resize'', function(){\r\n\r\n              map.fitBounds(bounds);\r\n\r\n            });\r\n\r\naddMarker({lat: 33.465906, lng: -111.902533, content: ''<div style="font-size: 12px; background-color: white;"><strong>McDowell Village</strong><br />8300 East McDowell Road<br />Scottsdale, AZ 85257<br/><a href="http://www.mcdowellvillage.com">click here for more info</a></div>'' });\r\n\r\n            directionsDisplay = new google.maps.DirectionsRenderer();\r\n\r\n            directionsDisplay.setMap(map);\r\n\r\n            directionsDisplay.setPanel(document.getElementById("directionsContent"));\r\n\r\n}        function addMarker(data) {\r\n\r\n          // Create the marker\r\n\r\n          var marker = new google.maps.Marker({\r\n\r\n            position: new google.maps.LatLng(data.lat, data.lng),\r\n\r\n            map: map,\r\n\r\n            icon: image\r\n\r\n          });\r\n\r\n\r\n\r\n          // Open the info window on marker click\r\n\r\n          google.maps.event.addListener(marker, "click", function() {\r\n\r\n            infowindow.setContent(data.content);\r\n\r\n            infowindow.open(map, marker);\r\n\r\n          });\r\n\r\n        }\r\n\r\n        window.onload = initialize\r\n\r\n        </script>\r\n\r\n\r\n<form id="directionsForm" action="#" onsubmit="calcRoute(); return false" class="multi-submit">\r\n\r\n  <label for="fromAddress" id="fromAddress_label">From:</label>\r\n\r\n  <input type="text" name="from" id="fromAddress" value="Enter Your Address" onfocus="clearText(this)" onblur="clearText(this)">\r\n\r\n\r\n  <label for="toAddress" id="toAddress_label">To:</label>\r\n\r\n  <input type="text" name="to" id="toAddress" value="8300 East McDowell Road, Scottsdale, AZ">\r\n\r\n  <input type="hidden" name="locale" value="en">\r\n\r\n  <input type="submit" name="submit-button" id="submit-button" value="Get Directions!">\r\n\r\n  <input type="button" id="printButton" value="Print this Page" onclick="window.print()">\r\n\r\n</form>\r\n\r\n\r\n\r\n<div id="directionsMap" style="position: relative; overflow: hidden; -webkit-transform: translateZ(0px); background-color: rgb(229, 227, 223);height:400px;width:100%; margin-top:15px;"><div class="gm-style" style="position: absolute; left: 0px; top: 0px; overflow: hidden; width: 100%; height: 100%; z-index: 0;"><div style="position: absolute; left: 0px; top: 0px; overflow: hidden; width: 100%; height: 100%; z-index: 0; cursor: url(http://maps.gstatic.com/mapfiles/openhand_8_8.cur) 8 8, default;"><div 